import DemoComponent from "./Component/DemoComponent";
import "./Component/DemoCss.css";
import { useState, useEffect } from "react";
import iron from "./image/iron.jpg";
import { Switch, Route, Redirect } from "react-router";
import { HashRouter } from "react-router-dom";
import { ToastContainer } from "react-toastify";

function App() {
  var [count, setCount] = useState(0);
  const aas = `count value ${count}`;
  const [a, b] = useState(aas);
  const onClickFunctiin = () => {
    if (count % 2 === 0) {
      setCount(count + 1);
      b(`button clicked ${count}`);
    } else {
      setCount(count + 1);
      b(`button clicked ${count}`);
    }
  };
  const as2 = "touch me";
  const [a1, b1] = useState(as2);
  const onWaiting = () => {
    b1("mat chhuo na !");
  };

  const resetCountVar = () => {
    setCount(0);
    b(`reseted to 0`);
  };
  const pointerLeave = () => {
    b1("ab chhu ke dikha");
  };
  const getFetchUsers = () => {
    this.setState(
      {
        loading: true,
      },
      () => {
        fetch("http://localhost:3000/posts")
          .then((res) => res.json())
          .then((result) =>
            this.setState({
              loading: false,
              users: result,
            })
          )
          .catch(console.log);
      }
    );
  };

  return (
    <div>
      <div className="div1">
        <p>{<DemoComponent />}</p>
      </div>

      <div className="div1">
        <h1>{a}</h1>
      </div>


      <div className="div1">
        <button
          onClick={onClickFunctiin}
          onDr={onWaiting}
          onPointerLeave={pointerLeave}
        >
          {a1}
        </button>
      </div>
      
      <div className="div2">
        <button onClick={resetCountVar}>reset the value</button>
      </div>
      
      <div className="div1">
        <a href="https:\\www.google.com" target="_blank">
          google karna hai
        </a>
      </div>
      {/* <ToastContainer
                autoClose={5000}
                hideProgressBar
                closeButton={<CloseButton/>}
            />
            <HashRouter>
                <Switch>
                    <Route path="/" exact render={() => <Redirect to="/app/main"/>}/>
                    <Route path="/a" exact component={DemoComponent}/>

                </Switch>
            </HashRouter> */}
    </div>
  );
}

export default App;

